package com.example.leetcode;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.assertEquals;

public class FindLongestLoopBackTest {
    @Test
    void testSolution(){
        FindLongestLoopBack solution = new FindLongestLoopBack();

        String result1 = solution.longestPalindrome("aacabdkacaa");
        assertEquals("aca", result1);

        String result2 = solution.longestPalindrome("");
        assertEquals("", result2);

        String result3 = solution.longestPalindrome("");
        assertEquals("", result3);
    }
}
